import { CREAT_TODO } from "./todo.actions";

export const initialStates = {
  todoList: [],
  deletedTodoList: [],
};

export const todoReducer = (state = initialStates, action) => {
  switch (action.type) {
    case CREAT_TODO:
      return {
        ...state,
        todoList: state.todoList.concat({
          id: Math.random(),
          title: action.payload,
          completed: false,
        }),
      };
    default:
      return state;
  }
};
