import Button from "../Button";
import Input from "../Input";
import { createDone, createDelete,createTodo } from "../../store/todo";

const TodoItemContainer = styled("li")`
  padding: 4px;
  background-color: ${({ completed }) => (completed ? "green" : "transparent")};
`;

const TodoItem = ({ title, completed, id }) => {
  const dispatch = useDispatch();
  const handleDone = (id) => {
    dispatch(createDone(id));
  };
  const handleDelete = (id) => {
    dispatch(createDelete(id));
  };

  const [edit, setEdit] = useState(null);
  //const [value, setValue] = useState("");

  const handleEdit = (id) => {
    setEdit(id);
   // setValue(title);
   console.log('dcsdc')
  };

  //const handleValue = useCallback((e) => {
  //  setValue(e.target.value);
  //}, []);

  return (
    <TodoItemContainer completed={completed}>
      <div key={id}>
        {
          edit === id ?
            <div>
              <input/>
              <button>fgdgdfg</button>
            </div>: <p>{title}</p>
        }


          <Button onClick={handleEdit} type="button">
            Редактировать
          </Button>
          <Button onClick={handleDone} type="button">
              Выполнено
          </Button>
          <Button onClick={handleDelete} type="button">
            Удалить
          </Button>
        </div>

    </TodoItemContainer>
  );
};

export default TodoItem;

