import React from "react";

import Button from "../Button";

const ListItem = ({ name, done, id , onDone, onDelete }) => {
    const handleDelete = () => {
      onDelete(id)
    }
    const handleDone = () => {
      onDone(id)

    }
  return (
    <li>
      <p>{name}</p>
      <div>
        <Button onClick={handleDone} variant="outlined" size="small" type="button">
          Выполнено
        </Button>
        <Button onClick={handleDelete} variant="outlined" size="small" type="button">
          Удалить
        </Button>
      </div>
    </li>
  );
};

export default ListItem;
