import React from "react";
import styled from "styled-components";
import { v4 as uuidv4 } from "uuid";

import Header from "./components/Header";
import Filter from "./components/Filter";
import List from "./components/List";
import Form from "./components/Form";

const TodoList = styled("div")`
  margin: 32px auto;
  padding: 8px;
  max-width: 600px;
  box-sizing: border-box;
  border: 2px solid gray;
`;

const skeletonTodo = {
  id: uuidv4(),
  name: "some todo",
  done: false,
};

class App extends React.Component {
  state = {
    filterStatus: '', // deleted, done
    filterValue: "",
    todoList: [skeletonTodo],
    deletedTodoList: [],
  };

  handleClick = (filterStatus) => {
    this.setState({
      filterStatus
    })
    console.log(filterStatus)
  }

  handleCreateTodo = (name) => {
    this.setState({
      todoList: this.state.todoList.concat({
        name,
        done: false,
        id: uuidv4(),
      }),
    });
  };

  handleDone = (id) => {
    this.setState((state) => ({
      todoList: state.todoList.map((todo) =>
        todo.id === id ? { ...todo, done: true } : todo
      ),
    }));
  };

  handleDelete = (id) => {
    this.setState((state) => {
      const deletedTodoIndex = state.todoList.findIndex(
        (todo) => todo.id === id
      );

      const deletedTodo = state.todoList.splice(deletedTodoIndex, 1);

      return {
        todoList: [...state.todoList],
        deletedTodoList: state.deletedTodoList.concat(deletedTodo),
      };
    });
  };

  render() {
    const { todoList } = this.state;
    console.log(this.state.todoList, this.state.deletedTodoList);
    return (
      <TodoList>
        <Header />

        {todoList.length > 0 && (
          <>
            <section>
              <Filter
              onClick={this.handleClick}
              />
            </section>
            <List
              onDone={this.handleDone}
              onDelete={this.handleDelete}
              list={todoList}
            />
          </>
        )}

        <section>
          <Form onCreateTodo={this.handleCreateTodo} />
        </section>
      </TodoList>
    );
  }
}

export default App;
