import React from "react";
import styled from "styled-components";

import Button from "../Button";
import Input from "../Input";

const FilterForm = styled("form")`
  padding: 12px 16px;
  display: flex;
  flex-direction: column;
  background-color: rgba(0, 0, 0, 0.1);
  border-radius: 8px;
`;

const ButtonsGroup = styled("div")`
  display: flex;
  flex-direction: row;
  justify-content: center;
  margin: 4px -4px 0;

  > button {
    margin: 4px;
    flex: 1;
  }
`;

const Filter = ({onClick}) => {

  const handleAllClick = () => {
    onClick('all')
    console.log(handleDoneClick)
  }

  const handleDoneClick = () => {
      onClick('Done')
    }

  const handleDeleteClick = () => {
        onClick('Delete')
      }

  return (
    <FilterForm>
      <Input
        label="Поиск по названию"
        id="search"
        placeholder="Начни вводить"
      />
      <ButtonsGroup>
        <Button onClick={handleAllClick} variant="contained" type="button">
          Все
        </Button>
        <Button onClick={handleDoneClick} variant="contained" type="button">
          Выполненные
        </Button>
        <Button onClick={handleDeleteClick} variant="contained" type="button">
          Удалённые
        </Button>
      </ButtonsGroup>
    </FilterForm>
  );
};

export default Filter;
