import React, { useState } from "react";
import styled from "styled-components";
import { useDispatch } from "react-redux";
import Button from "../Button";
import Input from "../Input";
import { createDone, createDelete } from "../../store/todo";

const TodoItemContainer = styled("li")`
  padding: 4px;
  background-color: ${({ completed }) => (completed ? "green" : "transparent")};
`;

const TodoItem = ({ title, completed, id }) => {
  const dispatch = useDispatch();
  const handleDone = (id) => {
    dispatch(createDone(id));
  };
  const handleDelete = (id) => {
    dispatch(createDelete(id));
  };

  const [edit, setEdit] = useState(null);
  const [value, setValue] = useState("");

  const handleEdit = (id, name) => {
    setEdit(id);
    setValue(name);
  };

  const handleValue = (e) => {
    setValue(e.target.value);
  };

  return (
    <TodoItemContainer completed={completed}>
      {edit === id ? (
        <div>
          <Input value={value} onChange={handleValue} />
        </div>
      ) : (
        <p>{title}</p>
      )}
      {edit === id ? (
        <div>
          <Button
            //onClick={handleSaveTodo}
            variant="outlined"
            size="small"
            type="button"
          >
            Сохранить
          </Button>
        </div>
      ) : (
        <div>
          <Button onClick={handleEdit} type="button">
            Редактировать
          </Button>
          {!completed && (
            <Button onClick={handleDone} type="button">
              Выполнено
            </Button>
          )}
          <Button onClick={handleDelete} type="button">
            Удалить
          </Button>
        </div>
      )}
    </TodoItemContainer>
  );
};

export default TodoItem;
