import React from "react";

import styled from "styled-components";

const PostWrapper = styled("div")`
  flex: 1 0 calc(25% - 8px);
  border: 1.5px solid gray;
  border-radius: 6px;
  box-sizing: border-box;
  padding: 8px;
  margin: 4px;

  > h6 {
    font-size: 16px;
    font-weight: 600;
    border-bottom: 1px solid gray;
    padding-bottom: 8px;
    margin-top: 0px;
    margin-bottom: 8px;
  }

  > p {
    margin-top: auto;
    font-size: 14px;
    font-weight: 300;
  }
`;

const Post = ({ title, body }) => {
  return (
    <PostWrapper>
      <h6>{title}</h6>
      <p>{body}</p>
    </PostWrapper>
  );
};

export default Post;
