import React from "react";

import Button from "../Button";

/*
  handleDone => this.props
  handleDelete => this.props
*/


const ListItem = ({ name, done, }) => {

  function handleDelete (e,id) {
    e.preventDefault();
    this.name = this.name.filter((user)=> user.id !== id)
  }

  return (
    <li>
      <p>{name}</p>
      <div>
        <Button variant="outlined" size="small" type="button">
          Выполнено
        </Button>
        <Button variant="outlined" size="small" type="button" onClick={handleDelete}>
          Удалить
        </Button>
      </div>
    </li>
  );
};

export default ListItem;
