import React from "react";

import styled from "styled-components";
import { getPosts } from "../../api/posts"; 
import Post from "../../components/Post";


const PostsWrapper = styled("section")`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  width: 100%;
  max-width: 800px;
  margin: 8px auto;
  padding: 4px;
  box-sizing: border-box;
`;

class Posts extends React.Component {
  state = { posts: [], loading: false, error: null };

  componentDidMount(){
    this.setState({
      loading: true
    })
    getPosts()
    .then((posts) =>{
      this.setState({
        posts,
        loading:false
      })

    } )
    .catch((err) => {
      console.log(err)
      this.setState({
        error: 'error from...'
      })
    })
  }

  render() {
    const { posts,loading,error } = this.state;

    if(loading === true){
      return <p>loading</p>
    }
    if(error){
      return <p>{error}</p>
    }

    return (
      <PostsWrapper>
        {posts.map((post) => (
          <Post key={post.id} {...post} />
        ))}
      </PostsWrapper>
    );
  }
}

export default Posts;
