import styled from "styled-components";

export const Head = styled("header")`
  background-color: red;

  > h1 {
    color: white;
  }
`;

export const P = styled("p")`
  color: ${(props) => props.color || "white"};
  font-size: ${({ fontSize }) => fontSize || "32px"};
`;
