}

  render(){
    const {name,lastName} = this.props;
    const {age} = this.state;
    return (
      <p>
        <button onClick={this.hendalClick}>click</button>
        <p>
        name - {name}, lastName - {lastName}, age - {age}
        </p>

      </p>  

    )
  }
}
