import React, { useState, useEffect } from "react";

import styled from "styled-components";
import { useParams, Link } from "react-router-dom";

import { getPost, getPostComments } from "../../api/posts";
import useRequest from "../../hooks/useRequest";

const PostDetailWrapper = styled("section")`
  display: flex;
  flex-direction: row;
  flex-wrap: wrap;
  width: 100%;
  max-width: 800px;
  margin: 8px auto;
  padding: 4px;
  box-sizing: border-box;
`;

const PostDetail = () => {

  const [post,loading,error] = useRequest(getPost)
  const [comments] = useRequest(getPostComments)


  return (

    <PostDetailWrapper>
       <Link to="/posts">Posts</Link>
      {loading && "loading..."}
      {error && "some error..."}
      {post && (
        <>
          <h1>{post.title}</h1>
          <p>{post.body}</p>
        </>
      )}
      {comments.map((todo) => 
          <div key={todo.id}>
          <ul>
            <li>{todo.name}</li>
            <li>{todo.email}</li>
            <li>{todo.body}</li>
          </ul>
          </div>
        )
      }



    </PostDetailWrapper>
  );
};

export default PostDetail;
