import React from "react";
import styled from "styled-components";
import { v4 as uuidv4 } from "uuid";

import Header from "./components/Header";
import Filter from "./components/Filter";
import List from "./components/List";
import Form from "./components/Form";

const TodoList = styled("div")`
  margin: 32px auto;
  padding: 8px;
  max-width: 600px;
  box-sizing: border-box;
  border: 2px solid gray;
`;

// const skeletonTodo = {
//   id: uuidv4(),
//   name: "some todo",
//   done: false,
// };

class App extends React.Component {
  state = {
    filterStatus: "all", // deleted, done
    filterValue: "",
    todoList: [],
    deletedTodo: [],
  };

  handleCreateTodo = (name) => {
    this.setState({
      todoList: this.state.todoList.concat({ name, done: false, id: uuidv4() }),
    });
  };

  handleDone = (id) => {
    console.log('handleDone')
  }

  handleDelete = (id) => {
    this.setState({
      todoList: this.state.todoList.filter((todo) => todo.id !== id),
    })
    console.log('handleDelete')
  }

  render() {
    const { todoList } = this.state;

    return (
      <TodoList>
        <Header />
        {todoList.length > 0 && (
          <>
            <section>
          <Filter />
        </section>
        <List onDone={this.handleDone} onDelete={this.handleDelete} list={todoList}/>
          </>
        )}
        <section>
          <Form onCreateTodo={this.handleCreateTodo} />
        </section>
      </TodoList>
    );
  }
}

export default App;
