import { useState, useEffect } from "react" 
import { useParams } from "react-router-dom"

const useRequest = (url,options) => {
    const params = useParams()
    const [status, setStatus] = useState({
        loading: false,
        data: undefined,
        error: undefined
    })

    function requestNom(url,options){
        setStatus({loading:true})
        fetch(url,options)
            .then((res) => res.json())
            .then((res) => {setStatus({loading:false, data: res.data})})
            .catch((error) => {setStatus({loading:false, error})})
    }   

    useEffect(() => {
        if(url){
            requestNom(url,options,params.id)
        }
    },[params.id])

    return {...status,requestNom}
}

export default useRequest
