import React, { useState, useCallback } from "react";
import styled from "styled-components";
import { useDispatch } from "react-redux";
import Form from "../../containers/TodoForm";
import Button from "../Button";
import Input from "../Input";
import {
  createDone,
  createDelete,
  createEdit,
  createSave,
} from "../../store/todo";

const TodoItemContainer = styled("li")`
  padding: 4px;
  background-color: ${({ completed }) => (completed ? "green" : "transparent")};
`;

const TodoItem = ({ title, completed, id }) => {
  const dispatch = useDispatch();
  const handleDone = () => {
    dispatch(createDone(id));
  };
  const handleDelete = () => {
    dispatch(createDelete(id));
  };

  const [mode, setMode] = useState("view");
  const [value, setValue] = useState(title);

  const handleValue = useCallback((e) => {
    setValue(e.target.value);
  }, []);

  const handleEdit = () => {
    setMode("edit");
  };

  const handleSave = () => {
    dispatch(createSave(value, id));
    setMode("view");
  };

  return (
    <TodoItemContainer completed={completed}>
      <div>
        {mode === "edit" ? (
          <form>
            <Input value={value} onChange={handleValue} id="task" />
            <Button onClick={handleSave} type="button">
              Сохранить
            </Button>
          </form>
        ) : (
          <p>{title}</p>
        )}

        <Button onClick={handleEdit} type="button">
          Редактировать
        </Button>
        <Button onClick={handleDone} type="button">
          Выполнено
        </Button>
        <Button onClick={handleDelete} type="button">
          Удалить
        </Button>
      </div>
    </TodoItemContainer>
  );
};

export default TodoItem;

