import React, { useState, useCallback } from "react";
import styled from "styled-components";

import Button from "../Button";

const Wrapper = styled("div")`
  margin: 40px auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  background-color: rgba(0, 0, 0, 0.1);
  border: 1px solid gray;
  border-radius: 8px;
  overflow: hidden;
  max-width: 250px;

  > p {
    width: 100%;
    margin: 0px;
    text-align: center;
    font-size: 24px;
    font-weight: 500;
    padding: 12px 0px;
  }
`;

const ButtonsGroup = styled("div")`
  width: 100%;
  display: flex;
  flex-direction: row;
  justify-content: space-between;
`;

const Counter = ({ initial = 0 }) => {
  const [count, setCount] = useState(initial);

  const handleAdd = useCallback(() => setCount((prev)=> prev + 1),[]);
  const handleMinus = useCallback(() => setCount((prev)=> prev - 1),[]);
  const handleReset = useCallback(() => setCount(initial),[initial]);



  return (
    <Wrapper>
      <p>{count}</p>
      <ButtonsGroup>
        <Button color="yellow" onClick={handleMinus}>
          -
        </Button>
        <Button color="green" onClick={handleReset}>
          reset
        </Button>
        <Button color="red" onClick={handleAdd}>
          +
        </Button>
      </ButtonsGroup>
    </Wrapper>
  );
};

function generateRandomNumbers() {
  const numbers = [];

  while (numbers.length < 20000) {
    const number = Math.floor(Math.random() * 1000000);
    if (!numbers.includes(number)) {
      numbers.push(number);
    }
  }

  return numbers;
}

export default Counter;
