import React, {useState}  from "react";

import styled from "styled-components";
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Modal from '@mui/material/Modal';

const UserContainer = styled("div")`
  flex: 0 0 calc(100% / 4 - 16px);
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 8px;
  padding: 8px;
  border: 1.5px solid grey;
  border-radius: 8px;
  box-sizing: border-box;

  > h6 {
    font-size: 16px;
    margin-top: 0px;
    margin-bottom: 12px;
  }

  > ul {
    list-style: none;
    margin: 0px;
    padding: 0px;
  }
`;

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
};


const User = ({ email, name, phone, picture, location, dob, gender}) => {
  const [open, setOpen] = useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  const { title, first, last } = name;
  const { city, country, street } = location;
  const { age, date} = dob;

  return (
    <UserContainer>
      <img width="100px" height="100px" alt={first} src={picture.medium} />
      <h6>
        {title} {first} {last}
      </h6>
      <ul>
        <li>
          location: {country}, {city}, {street.name}
        </li>
        <li>phone: {phone}</li>
        <li>email: {email}</li>
      </ul>

      <Button onClick={handleOpen}>Open modal</Button>
      <Modal
        open={open}
        onClose={handleClose}

      >
        <Box sx={style}>

          <ul>
        <li>{title} {first} {last}</li> 
        <li>dob: {age},{date}</li>  
        <li>gender: {gender}</li>   
        <li>
          location: {country}, {city}, {street.name}
        </li>
        <li>phone: {phone}</li>
        <li>email: {email}</li>
      </ul>


        </Box>
      </Modal>

    </UserContainer>
  );
};

export default User;
