import { CREAT_TODO, CREAT_DONE, CREAT_DELETE } from "./todo.actions";

export const initialStates = {
  todoList: [],
  deletedTodoList: [],
};

export const todoReducer = (state = initialStates, action) => {
  switch (action.type) {
    case CREAT_TODO:
      return {
        ...state,
        todoList: state.todoList.concat({
          id: Math.random(),
          title: action.payload,
          completed: false,
        }),
      };
    case CREAT_DONE:
      return {
        ...state,
        todoList: state.todoList.map((todo) => todo.id === action.payload),
      };
    case CREAT_DELETE:
      const deletedTodoIndex = state.todoList.findIndex(
        (todo) => todo.id === action.payload
      );
      const deletedTodo = state.todoList.splice(deletedTodoIndex, 1);
      return {
        ...state,
        todoList: [...state.todoList],
        deletedTodoList: state.deletedTodoList.concat(deletedTodo),
      };
    default:
      return state;
  }
};
