export const getSlice = (state) => state.todo;

export const  getList = (state) => {
    let list;

    if (state.filterStatus === "All") {
      list = state.todoList;
    }

    if (state.filterStatus === "Done") {
      list = state.todoList.filter((todo) => todo.completed === true);
    }

    if (state.filterStatus === "Delete") {
      list = state.deletedTodoList;

    }
    if (state.listfilterValue) {
      list = list.filter((todo) =>
        todo.title
          .toLocaleLowerCase()
          .includes(state.filterValue.toLocaleLowerCase())
      );
    }
    console.log(state.filterStatus)
    console.log(state.todoList)
    console.log(state.deletedTodoList)
    console.log(state.listfilterValue)
    return list;
  };
