import * as Statues from '../statuses'
import {
  SET_POSTS,
  SET_POSTS_REQUEST_STATUS_FAILURE,
  SET_POSTS_REQUEST_STATUS_PENDING
} from './posts.actions'

const initialState = {
  postsRequestStatus: Statues.UNCALLED,
  posts: [],
};

export const postsReducer = (state = initialState, action) => {
  switch (action.type) {
    case SET_POSTS:
      return {
        ...state,
        posts: action.payload,
        postsRequestStatus: Statues.SUCCESS
      }
      case SET_POSTS_REQUEST_STATUS_FAILURE:
        return {
          ...state,
          postsRequestStatus: Statues.FAILURE

        }
        case SET_POSTS_REQUEST_STATUS_PENDING:
          return {
            ...state,
            posts: Statues.PENDING
          }    
    default:
      return state;
  }
};
