import React, {useState, useEffect}  from "react";

import styled from "styled-components";
import Box from '@mui/material/Box';
import Button from '@mui/material/Button';
import Typography from '@mui/material/Typography';
import Modal from '@mui/material/Modal';

const UserContainer = styled("div")`
  flex: 0 0 calc(100% / 4 - 16px);
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 8px;
  padding: 8px;
  border: 1.5px solid grey;
  border-radius: 8px;
  box-sizing: border-box;

  > h6 {
    font-size: 16px;
    margin-top: 0px;
    margin-bottom: 12px;
  }

  > ul {
    list-style: none;
    margin: 0px;
    padding: 0px;
  }
`;

const style = {
  position: 'absolute',
  top: '50%',
  left: '50%',
  transform: 'translate(-50%, -50%)',
  width: 400,
  bgcolor: 'background.paper',
  border: '2px solid #000',
  boxShadow: 24,
  p: 4,
};


const User = ({ email, name, phone, picture, location,results }) => {
  const [open, setOpen] = useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);
  const { title, first, last } = name;
  const { city, country, street } = location;


  return (
    <UserContainer>
      <img width="100px" height="100px" alt={first} src={picture.medium} />
      <h6>
        {title} {first} {last}
      </h6>
      <ul>
        <li>
          location: {country}, {city}, {street.name}
        </li>
        <li>phone: {phone}</li>
        <li>email: {email}</li>
      </ul>

      <Button onClick={handleOpen}>Open modal</Button>
      <Modal
        open={open}
        onClose={handleClose}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <Typography id="modal-modal-title" variant="h6" component="h2">
          <ul>
        <li>
          </li>    
        <li>
          location: {country}, {city}, {street.name}
        </li>
        <li>phone: {phone}</li>
        <li>email: {email}</li>
      </ul>
          </Typography>

        </Box>
      </Modal>

    </UserContainer>
  );
};

export default User;
